mapper.select:
		select
		count(*) from i_com where 1=1

        	and biz_id=#{bizId}  


        	and name like CONCAT('%', #{name}, '%')


        	and brand like CONCAT('%', #{brand}, '%')


        	and state = #{status}

		select
		* from i_com where 1=1

        	and biz_id=#{bizId}  


        	and name like CONCAT('%', #{name}, '%')


        	and brand like CONCAT('%', #{brand}, '%')


        	and state = #{status}

		 limit #{start}, #{limit}
