mapper.select:
		select
		count(*) from i_com where 1=1

        	and biz_id=#{bizId}  


			and brand_id=#{brandId}


			and pric_cat_id=#{pricCatId}


			and pack_cat_id=#{packCatId}


        	and name like CONCAT('%', #{name}, '%')


        	and state = #{status}

		select
		* from i_com where 1=1

        	and biz_id=#{bizId}  


			and brand_id=#{brandId}


			and pric_cat_id=#{pricCatId}


			and pack_cat_id=#{packCatId}


        	and name like CONCAT('%', #{name}, '%')


        	and state = #{status}

		 limit #{start}, #{limit}

select
* from i_com where state = '1'

	and brand_id in 
	  #{item}



	and pric_cat_id in 
		#{item}



	and pack_cat_id in 
		#{item}


ORDER BY brand, retail_price_br
